    private void ingre_tel_mKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_ingre_tel_mKeyTyped
        if(ingre_tel_m.getText().length() >= 10)
        {
            evt.consume();
        
        }
        
        
        char c = evt.getKeyChar();
        
        if(c<'0' || c>'9') evt.consume();
    }//GEN-LAST:event_ingre_tel_mKeyTyped

    private void ing_edad_mKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_ing_edad_mKeyTyped
        if(ing_edad_m.getText().length() >= 2)
        {
            evt.consume();
        
        }
        
        
        char c = evt.getKeyChar();
        
        if(c<'0' || c>'9') evt.consume();
    }//GEN-LAST:event_ing_edad_mKeyTyped

